INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (85189, 'Etravirine', 'Diazepam', 'Moderate', 'Coadministration with etravirine may increase the plasma concentrations of drugs that are substrates of the CYP450 2C19 and/or 2C9 isoenzymes. The mechanism is decreased clearance due to inhibition of these isoenzymes by etravirine.', 'DDInter', 'Caution is advised if etravirine must be used concomitantly with medications that undergo metabolism by CYP450 2C19 and/or 2C9 , particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever etravirine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if etravirine must be used concomitantly with medications that undergo metabolism by CYP450 2C19 and/or 2C9 , particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85290/', '', 'Meprobamate, Lorazepam, Chlordiazepoxide, Halazepam, Hydroxyzine, Oxazepam', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Rilpivirine, Dolutegravir, Brincidofovir, More', 1767369485);
