INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (85195, 'Flumazenil', 'Diazepam', 'Moderate', 'The administration of flumazenil can result in convulsions in patients who have been receiving long-term treatment with benzodiazepines for epilepsy or sedation or who have recently received repeated doses of parenteral benzodiazepines. The mechanism is abrupt reversal of the benzodiazepine''s anticonvulsive effects. In addition, withdrawal seizures may occur in patients who are physically dependent on benzodiazepines.', 'DDInter', 'The use of flumazenil is not recommended in patients with epilepsy who have been receiving long-term treatment with benzodiazepines. In addition, flumazenil is contraindicated in patients who have received a benzodiazepine to control potentially life-threatening conditions such as status epilepticus or increased intracranial pressure. Extreme caution, dose individualization, and close monitoring is recommended for other patients. Clinicians should be prepared to manage convulsions. Most seizures have been successfully managed with benzodiazepines, phenytoin, or barbiturates.', 'Antagonism', 'The use of flumazenil is not recommended in patients with epilepsy who have been receiving long-term treatment with benzodiazepines.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/85296/', '', 'Meprobamate, Buspirone, Hydroxyzine', 'Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Deferoxamine, Levoleucovorin, Arginine, Methionine, Deferiprone, More', 1767369485);
