INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86252, 'Didanosine', 'Remdesivir', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', 'DDInter', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86353/', '', 'Paritaprevir, Glecaprevir, Simeprevir, Amprenavir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Etravirine, Dolutegravir, More', 'Paritaprevir, Glecaprevir, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, Oseltamivir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86253, 'Didanosine', 'Ribavirin', 'Major', 'Coadministration with ribavirin, a nucleoside analog, may increase exposure to didanosine (ddI) or its active triphosphate metabolite. Both the antiretroviral effect and clinical toxicities of ddI may be enhanced. The mechanism is inhibition of inosine 5''-monophosphate (IMP) dehydrogenase by an initial metabolite of ribavirin, resulting in decreased synthesis of guanine nucleotides that would otherwise compete with the active ddI metabolite for HIV-1 reverse transcriptase. In addition, ddI phosphorylation to the active metabolite may be enhanced by the accumulation of IMP, which is an efficient phosphate donor. Pharmacodynamically, the risk of mitochondrial toxicities such as lipodystrophy, peripheral neuropathy, pancreatitis, and lactic acidosis associated with ddI and other nucleoside reverse transcriptase inhibitors may also be increased during coadministration with ribavirin. Nucleoside analogs alone and in combination have been associated with these and other toxicities.', 'DDInter', 'The concomitant use of oral ribavirin and didanosine is considered contraindicated by US authorities.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86354/', '', 'Paritaprevir, Glecaprevir, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, Oseltamivir, More', 'Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, More', 1767369485);
