INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86306, 'Fenfluramine', 'Diethylpropion', 'Major', 'The efficacy of centrally-acting appetite suppressants in combination with other anorectic agents has not been studied, but available data suggest that combined use may have the potential for serious cardiac problems. Possible contributing factors include use for extended periods of time, higher than recommended dosages, and/or use in combination with other anorectic drugs.', 'DDInter', 'Centrally--acting appetite suppressants should not be used in combination with other anorectic agents, including prescription, over-the-counter, and herbal preparations. In addition, they are not recommended for patients who have used any anorectic agents within the prior year. The potential risk of possible serious adverse effects such as valvular heart disease and pulmonary hypertension should be assessed carefully against the benefit of weight loss, and baseline cardiac evaluation should be considered to detect preexisting valvular heart diseases or pulmonary hypertension prior to initiating treatment. These agents are not recommended for use in patients with known heart murmur or valvular heart disease, severe hypertension, or symptomatic cardiovascular disease including arrhythmias.', 'Synergism', 'Centrally--acting appetite suppressants should not be used in combination with other anorectic agents, including prescription, over-the-counter, and herbal preparations.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86407/', '', 'Orlistat, Naltrexone, Diethylpropion, Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Phenytoin, Ethosuximide, More', 'Ephedrine, Fenfluramine, Orlistat, Naltrexone', 1767369485);
