INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86332, 'Iobenguane (I-123)', 'Diethylpropion', 'Moderate', 'Coadministration with drugs that block norepinephrine uptake or deplete norepinephrine stores may decrease iobenguane I-123 uptake in neuroendocrine tumors and lead to false-negative imaging results.', 'DDInter', 'When medically feasible, any drug that blocks norepinephrine uptake or deplete norepinephrine stores should be discontinued for at least five biological half-lives before iobenguane I-123 administration.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86433/', '', 'Lorcaserin, Orlistat, Naltrexone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86333, 'Iobenguane (I-131)', 'Diethylpropion', 'Major', 'Coadministration with drugs that reduce catecholamine uptake or deplete catecholamine stores may interfere with iobenguane I-131 uptake into neuroendocrine tumors such as pheochromocytoma and paraganglioma that express high levels of norepinephrine transporter on their cell surfaces. Since iobenguane is similar in structure to norepinephrine and is subject to the same uptake and accumulation pathways as norepinephrine, drugs that alter norepinephrine disposition in adrenergic nerve terminals and presynaptic storage vesicles will likewise affect iobenguane.', 'DDInter', 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131. Do not administer these drugs until at least 7 days after each iobenguane I-131 dose. Patients should be monitored for the occurrence of clinically significant withdrawal symptoms, especially patients with elevated levels of circulating catecholamines and their metabolites.', 'Distribution', 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86434/', '', 'Lorcaserin, Sibutramine, Orlistat, Naltrexone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86334, 'Ioflupane I-123', 'Diethylpropion', 'Moderate', 'Drugs that bind to the dopamine transporter with high affinity may interfere with the image obtained using ioflupane I-123, which also binds to the dopamine transporter. These drugs include amoxapine, amphetamine, benztropine, bupropion, buspirone, cocaine, diethylpropion, ketamine, mazindol, methamphetamine, methylphenidate, modafinil, pemoline, phencyclidine (PCP), phentermine, phenylpropanolamine, pipradrol, selegiline, sertraline, and trihexyphenidyl. Selective serotonin reuptake inhibitors (SSRIs) and serotonin-norepinephrine reuptake inhibitors (SNRIs) may increase or decrease ioflupane binding to the dopamine transporter. Whether discontinuation of these drugs prior to ioflupane I-123 administration may help to minimize potential interference is unknown.', 'DDInter', 'Clinicians using ioflupane I-123 should be aware of possible diagnostic interference by drugs that can compete for binding to the dopamine transporter.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86435/', '', 'Ephedrine, Lorcaserin, Orlistat, Naltrexone', 'Indium In-111 pentetate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86335, 'Iohexol', 'Diethylpropion', 'Major', 'Intrathecal administration of iodinated contrast media may induce seizures. Although clinical data are generally lacking, there may be a theoretical risk of increased seizure potential when used with selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), and/or any substance that can reduce the seizure threshold (e.g., carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, theophylline). These agents are often individually epileptogenic and may have additive effects when combined.', 'DDInter', 'Drugs that can lower the seizure threshold should preferably be withheld for at least 48 hours prior to and 24 hours following intrathecal administration of iodinated contrast media, provided that temporary interruption of therapy does not pose an undue risk to the patient. Otherwise, close monitoring is advised during and after contrast administration.', 'Synergism', 'Drugs that can lower the seizure threshold should preferably be withheld for at least 48 hours prior to and 24 hours following intrathecal administration of iodinated contrast media, provided that temporary interruption of therapy does not pose an undue risk to the patient.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86436/', '', 'Lorcaserin, Orlistat, Naltrexone', 'Ioxilan, Iodipamide, Ioversol, Iodamide, Iodixanol, Tyropanoic acid, Ethiodized oil, Iopanoic acid, Iopodic acid, Iothalamic acid, Ioxaglic acid, More', 176736948