INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86524, 'Digoxin', 'Bepridil', 'Moderate', 'Bepridil may increase serum digoxin levels. The mechanism is unknown. In addition, the negative chronotropic effects these drugs have on heart rate may be additive.', 'DDInter', 'Patients should be closely monitored during coadministration and serum digoxin levels should be obtained when digoxin toxicity is suspected. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual changes, slow pulse, or irregular heartbeats. Calcium channel blockers such as felodipine, or amlodipine, isradipine, and nicardipine appear to affect digoxin levels to a lesser extent or not at all and may be considered as alternatives.', 'Others', 'Patients should be closely monitored during coadministration and serum digoxin levels should be obtained when digoxin toxicity is suspected.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86625/', '', 'Digitoxin', '', 1767369485);
