INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86544, 'Dihydrotachysterol', 'Digoxin', 'Moderate', 'Excessive dosing of vitamin D can induce hypercalcemia, which may increase the risk of digitalis toxicity and serious arrhythmias due to additive inotropic effects.', 'DDInter', 'Caution is advised if digitalis compounds are used concomitantly with vitamin D or vitamin D analogs. Serum digoxin, calcium, and phosphorus levels should be closely monitored, especially early in treatment during dosage adjustment. The serum calcium X phosphorus product should be maintained at less than 55 in patients with chronic kidney disease and should not be allowed to exceed 70 in any patient. An estimate of daily dietary calcium intake should be made and the intake adjusted when indicated. Patients should be advised to avoid an abrupt increase in dietary calcium intake, as it may trigger hypercalcemia, and to contact their physician if they experience early symptoms of vitamin D intoxication associated with hypercalcemia such as weakness, fatigue, headache, somnolence, vertigo, tinnitus, anorexia, nausea, vomiting, constipation, dry mouth, metallic taste, muscle pain, bone pain, ataxia, and hypotonia.', 'Synergism', 'Caution is advised if digitalis compounds are used concomitantly with vitamin D or vitamin D analogs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86645/', '', 'Digitoxin', 'Vitamin A, Beta carotene', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86545, 'Disopyramide', 'Digoxin', 'Moderate', 'Conflicting data indicate a potential interaction between digoxin and disopyramide. Limited data indicate that administration of disopyramide is followed by slightly increased serum digoxin levels, decreased serum half-life, and decreased volume of digoxin distribution.', 'DDInter', 'During coadministration, the patient should be monitored for digoxin toxicity and serum levels should be checked when necessary. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Others', 'During coadministration, the patient should be monitored for digoxin toxicity and serum levels should be checked when necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86646/', '', 'Tocainide', 'Digitoxin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86546, 'Dobutamine', 'Digoxin', 'Moderate', 'The concomitant use of sympathomimetic agents and cardiac glycosides may increase the risk of cardiac arrhythmias. The mechanism of this interaction is not known. Increased ectopic pacemaker activity has been reported to occur in patients taking digoxin in combination with pseudoephedrine.', 'DDInter', 'Caution should be exercised if these two drugs are coadministered. Electrocardiogram monitoring (ECG) is recommended. The use of epinephrine (adrenaline) with high doses of digitalis glycosides is not recommended.', 'Synergism', 'Caution should be exercised if these two drugs are coadministered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86647/', '', 'Droxidopa, Amrinone, Angiotensin II, Milrinone, Fenoldopam, Methoxamine, Mephentermine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86547, 'Dofetilide', 'Digoxin', 'Moderate', 'Concomitant administration of digoxin and dofetilide has been associated with a higher occurrence of torsade de pointes arrhythmia than dofetilide alone. It is not clear whether this finding represents an interaction between the drugs or the presence of more severe structural heart disease, a known risk factor for arrhythmia, in patients on digoxin.', 'DDInter', 'Clinicians should be aware of the potential for increased risk of torsade de pointes when dofetilide is administered in patients treated with digoxin.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86648/', '', 'Tocainide', 'Digitoxin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86548, 'Dolasetron', 'Digoxin', 'Major', 'Dolasetron has been shown to cause dose-dependent prolongation of the PR and QRS intervals. There have been reports of second- and third-degree atrioventricular block, cardiac arrest, and serious ventricular arrhythmias including fatalities in both adult and pediatric patients.', 'DDInter', 'Caution is advised if dolasetron is used concomitantly with other agents that prolong the PR or QRS interval, especially in the elderly and patients with known risk factors. An ECG should be obtained in these patients before initiating dolasetron and periodically during treatment. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat. Dolasetron should be avoided in patients with complete heart block or at risk for complete heart block, unless they have an implanted pacemaker.', 'Synergism', 'Caution is advised if dolasetron is used concomitantly with other agents that prolong the PR or QRS interval, especially in the elderly and patients with known risk factors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86649/', '', 'Nabilone, Dronabinol', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86549, 'Donepezil', 'Digoxin', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', 'DDInter', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86650/', '', 'Ginkgo biloba, Aducanumab', '', 1767369485);
