INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86572, 'Ethotoin', 'Digoxin', 'Moderate', 'Limited clinical data suggest that hydantoins may reduce digoxin serum levels. The mechanism of this interaction is unknown. Although phenytoin has been used in the treatment of digitalis-induced arrhythmias, it has been associated with cardiac arrest in patients with marked heart block.', 'DDInter', 'If these drugs are used concomitantly, close monitoring for clinical and laboratory evidence of altered digitalis effect is recommended. Occasionally, increases in the digoxin dosage may be necessary. The clinician should be aware that the risk of digoxin toxicity may be increased when a hydantoin is discontinued and should consider monitoring the patient more closely for those effects. Phenytoin should be avoided in patients with marked digoxin-induced heart block or bradycardia.', 'Others', 'If these drugs are used concomitantly, close monitoring for clinical and laboratory evidence of altered digitalis effect is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86673/', '', 'Tiagabine, Brivaracetam, Cannabidiol, Methsuximide, Paramethadione, Cenobamate, Perampanel, Fenfluramine, Phensuximide, Ganaxolone, Methylphenobarbital, More', '', 1767369485);
