INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86588, 'Gatifloxacin', 'Digoxin', 'Moderate', 'Some fluoroquinolones may significantly increase plasma digoxin levels. The mechanism has not been reported.', 'DDInter', 'Clinical and laboratory monitoring for altered digoxin efficacy and safety is recommended during coadministration. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats. Dose adjustments may be required if an interaction is suspected.', 'Others', 'Clinical and laboratory monitoring for altered digoxin efficacy and safety is recommended during coadministration.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86689/', '', 'Ganciclovir, Rifamycin, Netilmicin, Polymyxin B, Lomefloxacin, Sulfamethizole, Trifluridine, Benzylpenicillin, Sulfisoxazole, Amikacin, Levofloxacin, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86589, 'Gentamicin', 'Digoxin', 'Minor', 'Animal data show that serum digoxin levels increase after administration of nontoxic doses of gentamicin. The digoxin doses were five times that usually recommended for healthy human adults with normal renal function (on a per kg basis). Signs of digoxin toxicity were not apparent until toxic doses of gentamicin were given. These data may not apply to humans, and the clinical relevance is not clear. At this time, no significant clinical interaction is suspected in humans.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86690/', '', 'Digitoxin', 'Ganciclovir, Rifamycin, Netilmicin, Polymyxin B, Lomefloxacin, Sulfamethizole, Trifluridine, Benzylpenicillin, Sulfisoxazole, Amikacin, Levofloxacin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86590, 'Ginseng', 'Digoxin', 'Moderate', 'Asian and Siberian ginseng may interfere with digoxin either pharmacodynamically or with its monitoring via certain immunoassays. The interaction has been observed in vitro as well as in nondigoxin-treated mice that were fed ginseng and is thought to be due to substances in the ginseng that structurally resemble cardiac glycosides. Serum pools prepared from samples from patients receiving digoxin and then supplemented with Asian or Siberian ginseng also demonstrated interference.', 'DDInter', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. Use of ginseng should probably be avoided in patients treated with cardiac glycosides.', 'Others', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86691/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86591, 'Glasdegib', 'Digoxin', 'Moderate', 'Coadministration with glasdegib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) efflux transporters.', 'DDInter', 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range. Clinical and laboratory monitoring should be considered whenever glasdegib is added to or withdrawn from therapy with these drugs, and dosages adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Absorption', 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86692/', '', 'Digitoxin', 'Pertuzumab, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Belzutifan, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86592, 'Glycerin', 'Digoxin', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86693/', '', 'Alvimopan, Sorbitol, Naloxegol, Bisacodyl, Naldemedine, Methylnaltrexone, Lactitol, Sodium phosphate, monobasic, Prucalopride, Linaclotide, Plecanatide, More', '', 1767369485);
