INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86594, 'Heparin', 'Digoxin', 'Minor', 'Limited data reveal that the free fraction of digoxin is increased in uremic patients following administration of heparin after five minutes of hemodialysis. The mechanism is displacement of digoxin from albumin-binding sites. The resulting increase in digoxin was not associated with toxicity and was clinically insignificant. However, this interaction may be important as stable doses digoxin are established in some patients receiving heparin infusions. More data are needed.', 'DDInter', '-', 'Distribution', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86695/', '', 'Digitoxin', 'Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, More', 1767369485);
