INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86601, 'Isavuconazonium', 'Digoxin', 'Moderate', 'Coadministration with isavuconazonium sulfate (prodrug of isavuconazole) may increase the bioavailability of digoxin. The proposed mechanism is isavuconazole inhibition of the P-glycoprotein-mediated intestinal or renal efflux of digoxin.', 'DDInter', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of isavuconazonium sulfate. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Absorption', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of isavuconazonium sulfate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86702/', '', 'Digitoxin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86602, 'Isoprenaline', 'Digoxin', 'Moderate', 'The concomitant use of sympathomimetic agents and cardiac glycosides may increase the risk of cardiac arrhythmias. The mechanism of this interaction is not known. Increased ectopic pacemaker activity has been reported to occur in patients taking digoxin in combination with pseudoephedrine.', 'DDInter', 'Caution should be exercised if these two drugs are coadministered. Electrocardiogram monitoring (ECG) is recommended. The use of epinephrine (adrenaline) with high doses of digitalis glycosides is not recommended.', 'Synergism', 'Caution should be exercised if these two drugs are coadministered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86703/', '', 'Droxidopa, Amrinone, Angiotensin II, Milrinone, Fenoldopam, Methoxamine, Mephentermine, Mometasone furoate, Terbutaline, Umeclidinium, Aclidinium, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86603, 'Istradefylline', 'Digoxin', 'Moderate', 'Coadministration of istradefylline may increase the serum concentrations of digoxin. The proposed mechanism is istradefylline inhibition of the P-glycoprotein-mediated renal tubular secretion of digoxin.', 'DDInter', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of istradefylline, and the digoxin dosage adjusted as needed. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of istradefylline, and the digoxin dosage adjusted as needed.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86704/', '', 'Digitoxin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86604, 'Itraconazole', 'Digoxin', 'Major', 'Coadministration with itraconazole may significantly increase the serum concentrations of digoxin. The proposed mechanism is inhibition of the P-glycoprotein-mediated intestinal efflux or renal tubular secretion of digoxin by itraconazole, a potent inhibitor of the transporter.', 'DDInter', 'Caution is advised when digoxin is administered with itraconazole. The prescribing information for digoxin recommends decreasing the dosage by approximately 30% to 50% or modifying the dosing frequency when initiating itraconazole, followed by continued therapeutic monitoring of digoxin. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Absorption, Excretion', 'Caution is advised when digoxin is administered with itraconazole.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86705/', '', 'Digitoxin', 'Flucytosine, Anidulafungin, Micafungin, Oteseconazole', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86605, 'Ivabradine', 'Digoxin', 'Moderate', 'Coadministration of ivabradine with other drugs that can slow the heart rate such as beta-blockers, calcium channel blockers, and digitalis may increase the risk of excessive bradycardia or other conduction disturbances. Non-cardiac drugs that may also cause bradycardia include atazanavir, fingolimod, lacosamide, lithium, mefloquine, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', 'DDInter', 'Caution is advised when ivabradine is prescribed with other bradycardic drugs. Heart rate should be closely monitored, and the dosage of ivabradine adjusted accordingly. Patients should be advised to report significant decreases in heart rate or symptoms such as dizziness, lightheadedness, fatigue, and hypotension.', 'Synergism', 'Caution is advised when ivabradine is prescribed with other bradycardic drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86706/', '', 'Regadenoson, Ubidecarenone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86606, 'Ivacaftor', 'Digoxin', 'Moderate', 'Coadministration with ivacaftor may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these pathways due to inhibition by ivacaftor and its pharmacologically active M1 metabolite.', 'DDInter', 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86707/', '', 'Digitoxin', 'Doxapram, Elexacaftor', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86607, 'Digoxin', 'Kanamycin', 'Minor', 'Oral neomycin has been shown to decrease the absorption of oral digoxin. The effects of oral paromomycin and kanamycin are not known. The proposed mechanism is a neomycin-induced malabsorption syndrome resulting in reduced absorption of drugs. This effect may be offset by a subsequent decrease in digoxin metabolism. During concomitant therapy with any of these antibiotics, digoxin serum concentrations and efficacy should be monitored. Patients should be advised to notify their physician if they experience a worsening of their heart symptoms.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86708/', '', 'Digitoxin', 'Ganciclovir, Rifamycin, Netilmicin, Polymyxin B, Lomefloxacin, Sulfamethizole, Trifluridine, Benzylpenicillin, Sulfisoxazole, Amikacin, Levofloxacin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86608, 'Digoxin', 'Kaolin', 'Moderate', 'INTERVAL: Concurrent administration of kaolin-pectin may decrease the oral bioavailability of digitalis glycosides. The mechanism of interaction is unknown.', 'DDInter', 'To minimize the potential for interaction, patients should be advised to take kaolin-pectin at least 2 hours after digoxin if possible. The same precaution may be applicable during therapy with digitoxin, although clinical data are lacking.', 'Absorption', 'To minimize the potential for interaction, patients should be advised to take kaolin-pectin at least 2 hours after digoxin if possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86709/', '', 'Attapulgite', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86609, 'Digoxin', 'Ketoconazole', 'Moderate', 'Coadministration with ketoconazole may increase the serum concentrations of digoxin. The proposed mechanism is inhibition of the P-glycoprotein-mediated intestinal efflux or renal tubular secretion of digoxin by ketoconazole. Available data are conflicting.', 'DDInter', 'Caution is advised when digoxin is administered with ketoconazole. Serum digoxin levels should be checked regularly and the dosage adjusted accordingly, particularly following initiation or discontinuation of ketoconazole in patients who are stabilized on their digoxin regimen. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', 'Caution is advised when digoxin is administered with ketoconazole.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86710/', '', 'Digitoxin', 'Tinidazole, Ascorbic acid, Sulfamethoxazole, Ciclopirox, Sulfamethizole, Darunavir, Diazoxide, Econazole, Sulfadiazine, Povidone-iodine, Sulfisoxazole, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86610, 'Digoxin', 'Ketoprofen', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86711/', '', 'Digitoxin', 'Chondroitin sulfate, Glucosamine, Phenylbutazone, Tolazoline, Dimethyl sulfoxide, Phenylbutazone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86611, 'Digoxin', 'Labetalol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86712/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86612, 'Digoxin', 'Lacosamide', 'Moderate', 'Lacosamide has been shown to prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, digoxin, atazanavir, mefloquine) may result in elevated risk of conduction disturbances and atrioventricular block.', 'DDInter', 'Caution is advised if lacosamide is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.g., marked first-degree AV block, second-degree or higher AV block and sick sinus syndrome without pacemaker) or severe cardiac disease such as myocardial ischemia or heart failure. An ECG should be obtained in these patients before initiating lacosamide and after titration to steady-state.', 'Synergism', 'Caution is advised if lacosamide is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86713/', '', 'Tiagabine, Brivaracetam, Cannabidiol, Methsuximide, Paramethadione, Cenobamate, Perampanel, Fenfluramine, Phensuximide, Ganaxolone, Methylphenobarbital, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86613, 'Digoxin', 'Lanreotide', 'Moderate', 'Somatostatin analogs (e.g., octreotide, lanreotide) can decrease heart rate and may potentiate the bradycardic effects of drugs like beta-blockers, calcium channel blockers, digitalis glycosides, and certain antiarrhythmic agents (e.g., class I antiarrhythmics, adenosine, amiodarone).', 'DDInter', 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers. Heart rate and blood pressure should be monitored.', 'Synergism', 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86714/', '', 'Norethisterone, Nafarelin, Cetrorelix, Ganirelix, Gonadorelin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86614, 'Digoxin', 'Lansoprazole', 'Moderate', 'Proton pump inhibitors may increase the bioavailability of digoxin. The mechanism may involve a pH-dependent increase in gastrointestinal absorption of digoxin and/or inhibition by PPIs of the P-glycoprotein-mediated intestinal transport of digoxin. Coadministration with proton pump inhibitors (PPIs) for prolonged periods may increase the risk of digoxin toxicity. Chronic use of PPIs can induce hypomagnesemia. In patients with hypomagnesemia, toxicity may occur despite serum digoxin concentrations below 2.0 ng/mL because magnesium depletion sensitizes the myocardium to digoxin. The mechanism via which hypomagnesemia may occur during long-term PPI use is unknown, although changes in intestinal absorption of magnesium may be involved. Hypomagnesemia has been reported rarely in patients treated with PPIs for at least three months, but in most cases, after a year or more. Serious adverse events include tetany, seizures, tremor, carpopedal spasm, atrial fibrillation, supraventricular tachycardia, and abnormal QT interval; however, patients do not always exhibit these symptoms.', 'DDInter', 'Caution is advised if digoxin is prescribed in combination with PPIs. Pharmacologic response and serum digoxin levels should be monitored more closely following the addition or discontinuation of PPI therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats. Monitoring of serum magnesium levels is recommended prior to initiation of PPI therapy and periodically thereafter if prolonged treatment is anticipated or when combined with other agents that can cause hypomagnesemia (e.g., diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, tacrolimus). Patients should be advised to seek immediate medical attention if they develop potential signs and symptoms of hypomagnesemia such as palpitations, arrhythmia, muscle spasm, tremor, or convulsions. In children, abnormal heart rates may cause fatigue, upset stomach, dizziness, and lightheadedness. Magnesium replacement as well as discontinuation of the PPI may be required in some patients.', 'Absorption', 'Caution is advised if digoxin is prescribed in combination with PPIs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86715/', '', 'Digitoxin', 'Tinidazole, Bismuth subcitrate potassium, Rifabutin, Vonoprazan, Levofloxacin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86615, 'Digoxin', 'Lanthanum carbonate', 'Moderate', 'INTERVAL: Theoretically, lanthanum carbonate may chelate with certain drugs in the gastrointestinal tract, resulting in reduced oral bioavailability of those drugs during coadministration.', 'DDInter', 'To minimize the potential for interaction, drugs that are known to interact with antacids (e.g., ACE inhibitors, beta blockers, bisphosphonates, coumarin derivatives, digitalis glycosides, fluoroquinolones, iron, phenytoin, rifampin, tetracyclines, thyroid preparations, valproic acid) should not be taken within 2 hours of administration of lanthanum carbonate according to the product labeling.', 'Absorption', 'To minimize the potential for interaction, drugs that are known to interact with antacids (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86716/', '', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Difelikefalin, Levoleucovorin, Arginine, Methionine, Deferasirox, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86616, 'Digoxin', 'Lapatinib', 'Major', 'Coadministration with lapatinib may significantly increase the plasma concentrations of digoxin. The proposed mechanism is lapatinib inhibition of the P-glycoprotein-mediated intestinal efflux and/or renal tubular secretion of digoxin.', 'DDInter', 'Caution is advised if digoxin is used in combination with lapatinib. Serum digoxin levels and pharmacologic effects should be closely monitored during coadministration and following the discontinuation of lapatinib, and the digoxin dosage adjusted as necessary. If digoxin serum concentration is above 1.2 ng/mL, the digoxin dosage should be reduced by one-half. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Excretion', 'Caution is advised if digoxin is used in combination with lapatinib.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86717/', '', 'Entrectinib, Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Ribociclib, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86617, 'Digoxin', 'Lasmiditan', 'Moderate', 'Coadministration of lasmiditan and agents that lower heart rate may increase the risk of bradycardia.', 'DDInter', 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk. Consider monitoring heart rate in patients for whom the estimated decrease in heart rate may not be tolerated.', 'Synergism', 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86718/', '', 'Frovatriptan, Dihydroergotamine, Rimegepant, Eptinezumab, Eletriptan, Rizatriptan, Ubrogepant, Ergotamine, Naratriptan, Galcanezumab, Zolmitriptan, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86618, 'Digoxin', 'Lenalidomide', 'Moderate', 'Coadministration with lenalidomide may increase the plasma concentrations of digoxin. The mechanism of interaction is unknown.', 'DDInter', 'It may be appropriate to monitor pharmacologic response and serum digoxin levels more closely whenever lenalidomide is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience signs and symptoms of digoxin toxicity such as irregular heartbeats, slow pulse, nausea, anorexia, or visual changes.', 'Others', 'It may be appropriate to monitor pharmacologic response and serum digoxin levels more closely whenever lenalidomide is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86719/', '', 'Digitoxin', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 1767369485);
