INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86708, 'Digoxin', 'Quinapril', 'Moderate', 'Some ACE inhibitors may decrease the renal clearance of digoxin. Increased plasma digoxin levels may result. The proposed mechanism is reduced tubular secretion of digoxin. Patients with congestive heart failure (CHF) or renal impairment may have a greater risk of developing digoxin toxicity.', 'DDInter', 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86809/', '', 'Digitoxin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86709, 'Digoxin', 'Quinidine', 'Major', 'Quinidine significantly increases serum digoxin levels in more than 90% of patients. The proposed mechanism is quinidine inhibition of the P-glycoprotein-mediated intestinal efflux and/or renal tubular secretion of digoxin.', 'DDInter', 'Caution is advised if digoxin must be used in combination with quinidine. Empiric reduction in digoxin dosing may be appropriate at the initiation of quinidine therapy. Serum digoxin levels and pharmacologic effects should be closely monitored, and the dosage adjusted accordingly. Patients should be advised to notify their physician if they experience signs of digitalis toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'Caution is advised if digoxin must be used in combination with quinidine.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86810/', '', 'Tocainide', '', 1767369485);
