INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86717, 'Digoxin', 'Rifampicin', 'Moderate', 'Coadministration with rifampin may decrease the plasma concentrations of digoxin. The proposed mechanism is induction of the P-glycoprotein-mediated intestinal efflux and renal tubular secretion of digoxin by rifampin.', 'DDInter', 'Caution is advised if digoxin is used in combination with rifampin. Digoxin levels should be checked more frequently and the dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of rifampin in patients who are stabilized on their digoxin regimen. Patients should be advised to notify their physician if they experience a worsening of their heart symptoms.', 'Excretion', 'Caution is advised if digoxin is used in combination with rifampin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86818/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Rifapentine, Streptomycin, Cycloserine, Rifabutin, Pyridoxine, Ethionamide, Pretomanid', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86718, 'Digoxin', 'Ritonavir', 'Moderate', 'Coadministration with ritonavir may increase serum digoxin concentrations and the risk of digoxin toxicity. The proposed mechanism is reduced clearance of digoxin due to ritonavir inhibition of p-glycoprotein-mediated renal tubular secretion.', 'DDInter', 'Caution is advised if digoxin must be used in combination with ritonavir. Pharmacologic response and serum digoxin levels should be monitored more closely whenever ritonavir is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'Caution is advised if digoxin must be used in combination with ritonavir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86819/', '', 'Digitoxin', 'Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Remdesivir, Elbasvir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86719, 'Digoxin', 'Rivastigmine', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', 'DDInter', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86820/', '', 'Ginkgo biloba, Aducanumab', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86720, 'Digoxin', 'Rolapitant', 'Moderate', 'Coadministration with rolapitant may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter.', 'DDInter', 'Caution is advised when rolapitant is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever rolapitant is added to or withdrawn from therapy.', 'Absorption', 'Caution is advised when rolapitant is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86821/', '', 'Digitoxin', 'Nabilone, Dronabinol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86721, 'Digoxin', 'Rufinamide', 'Moderate', 'Rufinamide may modestly or moderately decrease the plasma concentrations and therapeutic effects of drugs that are CYP450 3A4 substrates. The mechanism is enhanced metabolism by CYP450 3A4 enzyme induction.', 'DDInter', 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose. Dose adjustments of the coadministered drug may be required if an interaction is suspected.', 'Metabolism', 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86822/', '', 'Digitoxin', 'Tiagabine, Brivaracetam, Cannabidiol, Methsuximide, Paramethadione, Cenobamate, Perampanel, Fenfluramine, Phensuximide, Ganaxolone, Methylphenobarbital, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86722, 'Digoxin', 'Ruxolitinib', 'Moderate', 'Coadministration with ruxolitinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein efflux transporter and/or the breast cancer resistance protein (BRCP) in the intestine. The mechanism is decreased clearance via these routes due to inhibition by ruxolitinib.', 'DDInter', 'Caution is advised if ruxolitinib must be used concurrently with medications that are substrates of the P-glycoprotein efflux transporter and/or the breast cancer resistance protein (BRCP) , particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ruxolitinib is added to or withdrawn from therapy. The interaction may possibly be avoided or minimized if the doses of the affected medications are separated for as long as possible.', 'Excretion', 'Caution is advised if ruxolitinib must be used concurrently with medications that are substrates of the P-glycoprotein efflux transporter and/or the breast cancer resistance protein (BRCP) , particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86823/', '', 'Digitoxin', 'Entrectinib, Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Ribociclib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86723, 'Digoxin', 'Salsalate', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may increase plasma digoxin concentrations and half-life. The exact mechanism is unknown, but may be related to reduced renal clearance of digoxin. The interaction has been reported with indomethacin and ibuprofen, but data for other NSAIDs are not available.', 'DDInter', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels. The digoxin dosage may require adjustment. Patients should be advised to notify their physician if they experience nausea, anorexia, visual changes, slow pulse, or irregular heartbeats.', 'Excretion', 'Patients who require concomitant therapy should be monitored for altered pharmacologic effects of digoxin and for increased plasma levels.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86824/', '', 'Digitoxin', 'Ziconotide, Methoxyflurane', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86724, 'Digoxin', 'Saquinavir', 'Major', 'Saquinavir in combination with ritonavir has been shown to prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, digoxin, lacosamide, mefloquine, verapamil) may result in elevated risk of conduction disturbances and atrioventricular block. Coadministration with saquinavir may significantly increase the serum concentrations of digoxin. The mechanism of interaction has not been described but may be related to saquinavir inhibition of the P-glycoprotein-mediated renal tubular secretion of digoxin.', 'DDInter', 'Ritonavir-boosted saquinavir should generally not be used with other agents that prolong the PR interval. Extreme caution is advised if digoxin must be used concurrently with saquinavir. Serum digoxin levels should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of saquinavir in patients who are stabilized on their digoxin regimen. Patients should be advised to notify their physician if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Synergism, Excretion', 'Ritonavir-boosted saquinavir should generally not be used with other agents that prolong the PR interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86825/', '', 'Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Remdesivir, Elbasvir, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86725, 'Digoxin', 'Sarecycline', 'Moderate', 'Coadministration with sarecycline may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, kidney, and/or liver due to inhibition of P-gp-mediated efflux by sarecycline.', 'DDInter', 'Caution is advised when sarecycline is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever sarecycline is added to or withdrawn from therapy.', 'Others', 'Caution is advised when sarecycline is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86826/', '', 'Digitoxin', 'Omadacycline, Eravacycline', 1767369485);
