INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86745, 'Digoxin', 'Telaprevir', 'Moderate', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may increase serum digoxin concentrations and the risk of digoxin toxicity. The proposed mechanism is reduced clearance of digoxin due to inhibition of P-glycoprotein-mediated renal tubular secretion.', 'DDInter', 'Caution is advised if digoxin must be used in combination with boceprevir or telaprevir. Pharmacologic response and serum digoxin levels should be monitored more closely whenever the HCV NS3/4A protease inhibitor is added to or withdrawn from therapy, and the digoxin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience potential signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'Caution is advised if digoxin must be used in combination with boceprevir or telaprevir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86846/', '', 'Digitoxin', 'Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Remdesivir, Elbasvir, More', 1767369485);
