INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86747, 'Digoxin', 'Telmisartan', 'Moderate', 'Coadministration with telmisartan may modestly increase the serum concentrations of digoxin. The exact mechanism of interaction is unknown. These changes are not thought to be clinically significant, although patients with serum digoxin levels at the upper end of the therapeutic range may be at increased risk for digoxin toxicity. Digoxin did not affect the steady-state pharmacokinetics of telmisartan, and coadministration had no effect on the safety and tolerability of either drug.', 'DDInter', 'Pharmacologic response and serum digoxin levels should be monitored more closely following initiation, discontinuation, or change of dosage of telmisartan in patients who are stabilized on their digoxin regimen. Patients should be advised to contact their physician if they experience signs and symptoms of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Others', 'Pharmacologic response and serum digoxin levels should be monitored more closely following initiation, discontinuation, or change of dosage of telmisartan in patients who are stabilized on their digoxin regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86848/', '', 'Digitoxin', 'Olmesartan, Azilsartan medoxomil, Candesartan, Eprosartan, Sacubitril, Azilsartan medoxomil, Candesartan, Eprosartan, Olmesartan', 1767369485);
