INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86763, 'Digoxin', 'Trandolapril', 'Moderate', 'Some ACE inhibitors may decrease the renal clearance of digoxin. Increased plasma digoxin levels may result. The proposed mechanism is reduced tubular secretion of digoxin. Patients with congestive heart failure (CHF) or renal impairment may have a greater risk of developing digoxin toxicity.', 'DDInter', 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored. Patients should be advised to notify their physician if they experience nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeats.', 'Excretion', 'While this combination has demonstrated benefits in some CHF patients, their clinical response and digoxin levels should be monitored.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86864/', '', 'Digitoxin', '', 1767369485);
