INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86798, 'Belladonna', 'Phenylephrine', 'Moderate', 'The pressor response to phenylephrine may be potentiated by the vagolytic effect of atropine, which inhibits the reflex bradycardia that would normally accompany any increases in blood pressure induced by phenylephrine. Other antimuscarinic agents may also participate in this interaction, although clinical data are lacking.', 'DDInter', 'Caution is advised if phenylephrine (systemic or ophthalmic) is used in combination with atropine or other antimuscarinic agents. Cardiovascular status, including blood pressure and heart rate, should be closely monitored. When using ophthalmic formulations, measures to minimize systemic absorption should be employed, such as digital compression of the lacrimal sac or lid closure after instillation. A milder strength (< 10%) is preferable if phenylephrine ophthalmic solution is given.', 'Synergism', 'Caution is advised if phenylephrine (systemic or ophthalmic) is used in combination with atropine or other antimuscarinic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86899/', '', 'Tropicamide, Ephedrine, Ketorolac, Flunisolide, Betamethasone, Triamcinolone, Azelastine, Ciclesonide, Mupirocin, Hydrocortisone, Cromoglicic acid, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (86799, 'Duloxetine', 'Phenylephrine', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', 'DDInter', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/86900/', '', 'Homatropine, Atropine, Methscopolamine, Tropicamide, Mupirocin, Ciclesonide, Mometasone furoate, Mometasone, Flunisolide, Olopatadine, Vitamin A, More', 'Citalopram, Sertraline, Paroxetine, Maprotiline, Fluoxetine, St. John''s Wort, Nefazodone, Oxitriptan, Mirtazapine, Viloxazine, Vortioxetine, More', 1767369485);
