INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (87000, 'Dihydroergotamine', 'Voriconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 including azole antifungal agents may significantly increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. Clinical ergotism has been reported, which can lead to gangrene and myocardial infarction in severe cases.', 'DDInter', 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with itraconazole, ketoconazole, posaconazole, or voriconazole is considered contraindicated. Some authorities consider concomitant administration of ergot derivatives and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole. Use with other azole antifungal agents, including fluconazole at high multiple doses, should probably be avoided if possible.', 'Metabolism', 'Given the potential for ergot toxicity characterized by peripheral vasospasm, ischemia, thrombosis, tachycardia and hypertension, concomitant use of ergot derivatives with itraconazole, ketoconazole, posaconazole, or voriconazole is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/87101/', '', 'Frovatriptan, Eptinezumab, Lasmiditan, Rizatriptan, Naratriptan, Galcanezumab, Zolmitriptan, Sumatriptan, Erenumab, Fremanezumab', 'Caspofungin, Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 1767369485);
