INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (87858, 'Dipyridamole', 'Turmeric', 'Minor', 'Theoretically, turmeric may potentiate the effects of anticoagulants, platelet inhibitors, and thrombolytic agents, possibly increasing the risk of bleeding. In vitro data suggest that turmeric may inhibit platelet-activating factor and platelet aggregation. Patients should consult a healthcare provider before taking any herbal or alternative medicine. In patients who have used turmeric extensively prior to receiving anticoagulation, antiplatelet, or thrombolytic therapy, the potential for an interaction should be considered. Clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/87959/', '', 'Protein C, Acenocoumarol, Selexipag', '', 1767369485);
