INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88127, 'Esketamine', 'Methadone', 'Moderate', 'Concomitant use of esketamine with central nervous system (CNS) depressants may increase sedation and impairment of attention, judgment, thinking, reaction speed, and psychomotor skills.', 'DDInter', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants. Due to the risk of delayed or prolonged sedation and other adverse effects, patients should be monitored for at least 2 hours after esketamine administration, followed by an assessment to determine when the patient is considered clinically stable and ready to leave the healthcare setting. Patients should be instructed not to engage in potentially hazardous activities, such as driving a motor vehicle or operating machinery, until the next day after a restful sleep.', 'Synergism', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88228/', '', 'Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', 'Etomidate', 1767369485);
