INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88150, 'Flutamide', 'Methadone', 'Major', 'Methadone may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. High dosages of methadone alone have been associated with QT interval prolongation and torsade de pointes.', 'DDInter', 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If taking drugs that also cause CNS-depressant or orthostatic effects (e.g., sedating antihistamines, psychotropic drugs like tricyclic antidepressants, phenothiazines, and neuroleptics), patients should be made aware of the possibility of additive effects with methadone and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88251/', '', 'Diamorphine, Acamprosate, Varenicline, Disulfiram, Nalmefene, Nicotine, Codeine, Morphine, Oxymorphone, Tapentadol, Oxycodone, More', 'Darolutamide, Anastrozole, Fulvestrant, Letrozole, Exemestane', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88151, 'Fospropofol', 'Methadone', 'Moderate', 'Additive central nervous system and cardiorespiratory depressant effects may occur when fospropofol or propofol is administered with other depressants such as sedative-hypnotic agents and narcotic analgesics.', 'DDInter', 'Patients should be monitored closely for excessive sedation and cardiorespiratory depression, and the medication dosage(s) adjusted accordingly. Supportive therapy should be provided if needed.', 'Synergism', 'Patients should be monitored closely for excessive sedation and cardiorespiratory depression, and the medication dosage(s) adjusted accordingly.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88252/', '', 'Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Tapentadol, Acetaminophen, Ibuprofen, More', '', 1767369485);
