INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88175, 'Ibutilide', 'Methadone', 'Major', 'Like other class III antiarrhythmic agents, ibutilide can cause dose-related QT interval prolongation. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if ibutilide is used in combination with other drugs that can prolong the QT interval. Ibutilide should only be administered in a setting of continuous ECG monitoring and by personnel trained in identification and treatment of acute ventricular arrhythmias, particularly polymorphic ventricular tachycardia such as torsade de pointes.', 'Synergism', 'Caution is recommended if ibutilide is used in combination with other drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88276/', '', 'Moricizine, Tocainide, Bretylium, Mexiletine', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Oxymorphone, Oxycodone, Pentazocine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88176, 'Idarubicin', 'Methadone', 'Major', 'Methadone may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. High dosages of methadone alone have been associated with QT interval prolongation and torsade de pointes.', 'DDInter', 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If taking drugs that also cause CNS-depressant or orthostatic effects (e.g., sedating antihistamines, psychotropic drugs like tricyclic antidepressants, phenothiazines, and neuroleptics), patients should be made aware of the possibility of additive effects with methadone and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88277/', '', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Oxymorphone, Oxycodone, Pentazocine, More', 'Valrubicin, Plicamycin, Ixabepilone, Mitoxantrone, Bleomycin, Mitomycin, Dactinomycin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88177, 'Idelalisib', 'Methadone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of methadone, which is metabolized primarily by CYP450 3A4, 2B6, 2C19, and to a lesser extent by CYP450 2C9 and 2D6. The possibility of prolonged and/or increased pharmacologic effects of methadone, such as central nervous system and respiratory depression, should be considered. In addition, high dosages (particularly above 200 mg/day) and serum levels of methadone have been associated with QT interval prolongation and torsade de pointes arrhythmia.', 'DDInter', 'Caution is advised if methadone is prescribed in combination with CYP450 3A4 inhibitors. Pharmacologic response to methadone should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of the CYP450 3A4 inhibitor in patients who are stabilized on their methadone regimen. Patients should be advised to report excessive drowsiness, nausea, or asthenia to their physician, and to seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. ECG monitoring should be considered for patients on methadone with heart or liver disease; conduction abnormalities; electrolyte disturbances (i.e., hypokalemia, hypomagnesemia); concomitant use of drugs that may cause QT prolongation or electrolyte loss; concomitant use of CYP450 3A4 inhibitors; or use of methadone at dosages greater than 200 mg daily.', 'Metabolism', 'Caution is advised if methadone is prescribed in combination with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88278/', '', 'Diamorphine, Acamprosate, Lofexidine, Varenicline, Nalmefene, Nicotine, Morphine, Oxymorphone, Tapentadol, Meperidine, Hydromorphone, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Zanubrutinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88178, 'Ifosfamide', 'Methadone', 'Moderate', 'Centrally-acting agents such as antiemetics, sedatives, narcotics, or antihistamines may add to the neurotoxic effects of ifosfamide. Administration of ifosfamide can cause CNS toxicity, which necessitates careful monitoring of the patient. Neurologic manifestations include somnolence, confusion, hallucinations, blurred vision, psychotic behavior, extrapyramidal symptoms, urinary incontinence, seizures, and in some instances, coma. There have also been reports of peripheral neuropathy. Ifosfamide neurotoxicity may occur within a few hours to a few days after initial administration and typically resolves within 48 to 72 hours of ifosfamide discontinuation.', 'DDInter', 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide. If ifosfamide-induced encephalopathy occurs, both ifosfamide and non-essential concomitant CNS agents should be discontinued. Supportive therapy should be initiated and maintained until complete resolution of neurotoxicity.', 'Synergism', 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88279/', '', 'Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Acetylsalicylic acid, Naloxone, Naltrexone', 'Temozolomide, Thiotepa, Carmustine, Cyclophosphamide, Busulfan, Streptozocin, Chlorambucil, Lomustine, Dacarbazine, Mechlorethamine, Bendamustine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88179, 'Iloperidone', 'Methadone', 'Major', 'Iloperidone may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of iloperidone with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should have regular ECGs and be monitored for arrhythmias when QT interval is prolonged. Persistent QTc measurements exceeding 500 msec will require suspension of iloperidone therapy and immediate action to correct any concomitant risk factors before resuming treatment.', 'Synergism', 'Coadministration of iloperidone with other drugs that can prolong the QT interval should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88280/', '', 'Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', '', 1767369485);
