INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88198, 'Isradipine', 'Methadone', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88299/', '', 'Acamprosate, Naltrexone, Varenicline, Disulfiram, Levacetylmethadol, Nalmefene, Nicotine, Naltrexone, Tapentadol, Oxymorphone, Dezocine, Naloxone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88199, 'Itraconazole', 'Methadone', 'Major', 'Coadministration with itraconazole may significantly increase the plasma concentrations of methadone. The mechanism is itraconazole inhibition of methadone metabolism via CYP450 3A4. The use of methadone has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsades de pointes as well as cardiac arrest and sudden death.', 'DDInter', 'Given the potential for severe central nervous system and respiratory depression as well as life-threatening adverse cardiac events associated with increased plasma levels of methadone, concomitant use with itraconazole is considered contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88300/', '', 'Diamorphine, Acamprosate, Lofexidine, Varenicline, Disulfiram, Nalmefene, Nicotine, Oxymorphone, Tapentadol, Meperidine, Dezocine, More', 'Caspofungin, Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88200, 'Ivabradine', 'Methadone', 'Major', 'Due to its bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased when ivabradine is used with drugs that prolong the QT interval.', 'DDInter', 'Coadministration of ivabradine with drugs that can prolong the QT interval should generally be avoided. Caution and cardiac monitoring are recommended if concomitant use is required.', 'Synergism', 'Coadministration of ivabradine with drugs that can prolong the QT interval should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88301/', '', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Oxymorphone, Oxycodone, Pentazocine, More', 'Indomethacin, Regadenoson, Alprostadil, Ubidecarenone', 1767369485);
