INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88261, 'Methohexital', 'Methadone', 'Moderate', 'Narcotic analgesics may reduce the dosage of barbiturate anesthetics needed to induce anesthesia by as much as 40%. Additionally, apnea is more common with this combination.', 'DDInter', 'Respiratory and cardiovascular status should be closely monitored, and anesthetic dosages titrated accordingly.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88362/', '', 'Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Opium, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', 'Nitrazepam, Etomidate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88262, 'Methotrimeprazine', 'Methadone', 'Major', 'Methadone may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. High dosages of methadone alone have been associated with QT interval prolongation and torsade de pointes.', 'DDInter', 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If taking drugs that also cause CNS-depressant or orthostatic effects (e.g., sedating antihistamines, psychotropic drugs like tricyclic antidepressants, phenothiazines, and neuroleptics), patients should be made aware of the possibility of additive effects with methadone and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88363/', '', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Levacetylmethadol, Nalmefene, Nicotine, Naltrexone, Acetaminophen, Dezocine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88263, 'Methoxyflurane', 'Methadone', 'Moderate', 'Opioids may be expected to decrease the minimum alveolar concentration required for inhalation anesthetics. When these agents are used concomitantly, less inhalation anesthetic may be required.', 'DDInter', 'In general, anesthesia should be titrated to clinical effect by those experienced in anesthesia technique and in the care of patients receiving opioids.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88364/', '', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Levacetylmethadol, Nalmefene, Nicotine, Naltrexone, Tapentadol, More', 'Diflunisal, Salicylic acid, Choline salicylate, Salsalate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88264, 'Methscopolamine', 'Methadone', 'Moderate', 'Coadministration of opioids with anticholinergic agents may result in additive central nervous system (CNS), gastrointestinal, and genitourinary effects. The risk and/or severity of adverse effects such as sedation, dizziness, confusion, cognitive and psychomotor impairment, dry mouth, constipation, and urinary retention may increase. Severe constipation may lead to paralytic ileus in some cases.', 'DDInter', 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88365/', '', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Ibuprofen, Celecoxib, More', 'Tropicamide, Ephedrine, Ketorolac, Phenylephrine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88265, 'Methsuximide', 'Methadone', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88366/', '', 'Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', 'Lacosamide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88266, 'Methylene blue', 'Methadone', 'Major', 'Coadministration of monoamine oxidase inhibitors (MAOIs) with certain opioids has been associated with rare reports of severe and fatal adverse reactions. There appear to be two types of interaction, an excitatory and a depressive one. Symptoms of the excitatory reaction may include agitation, headache, diaphoresis, hyperpyrexia, flushing, shivering, myoclonus, rigidity, tremor, diarrhea, hypertension, tachycardia, seizures, and coma. The exact mechanism of interaction is unknown, but may involve excessive serotonergic activity in the central nervous system (i.e., serotonin syndrome). The interaction is unpredictable and has been reported primarily with meperidine or fentanyl and various MAOIs including phenelzine, tranylcypromine, linezolid, moclobemide, and selegiline. Fatal hyperpyrexia and hypertension have also been observed in animal studies with meperidine and phenelzine or furazolidone. In contrast, symptoms of the depressive reaction probably stem from potentiation of CNS effects by MAOIs and include respiratory depression, cyanosis, hypotension, and coma.', 'DDInter', 'Meperidine and fentanyl should not be used with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). Some manufacturers of MAOIs also contraindicate the concomitant use of propoxyphene and methadone due to their possible serotonergic effects. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with these opioids. Although morphine may also have significant CNS interactions with MAOIs, it is generally considered a safer alternative in patients treated with MAOIs who require a narcotic analgesic.', 'Others', 'Meperidine and fentanyl should not be used with MAOIs or other agents that possess MAOI activity (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88367/', '', 'Acamprosate, Lofexidine, Naltrexone, Varenicline, Nalmefene, Nicotine, Naltrexone, Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Cobicistat, Deferoxamine, Levoleucovorin, Arginine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88267, 'Metoclopramide', 'Methadone', 'Moderate', 'By diminishing gastrointestinal motility, narcotic analgesics may antagonize the pharmacologic effects of gastrointestinal prokinetic agents. In addition, concomitant use may increase central nervous system effects such as sedation, dizziness, confusion, and mental depression. Gastrointestinal prokinetic agents may alter the absorption characteristics of some controlled release narcotic analgesic preparations.', 'DDInter', 'The potential for reduced efficacy of gastrokinetic agents should be considered during coadministration with opioid analgesics. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Antagonism, Absorption', 'The potential for reduced efficacy of gastrokinetic agents should be considered during coadministration with opioid analgesics.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88368/', '', 'Nalmefene, Disulfiram, Acamprosate, Naltrexone, Naltrexone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88268, 'Metolazone', 'Methadone', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88369/', '', 'Nalmefene, Disulfiram, Acamprosate, Tapentadol, Naloxone, Dezocine, Oxymorphone', 'Trichlormethiazide, Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, Clindamycin, Trichlormethiazide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88269, 'Metoprolol', 'Methadone', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88370/', '', 'Penbutolol, Carteolol, Penbutolol', 'Nalmefene, Disulfiram, Levacetylmethadol, Naltrexone, Tapentadol, Naltrexone, Oxymorphone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88270, 'Metreleptin', 'Methadone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of opioids that are metabolized by the isoenzyme such as butorphanol, fentanyl, hydrocodone, methadone, and oxycodone. Reduced efficacy or withdrawal symptoms may occur in patients maintained on their narcotic pain regimen following the addition of a CYP450 3A4 inducer. Conversely, discontinuation of the inducer may increase opioid plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 'Pharmacologic response to the opioid should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the opioid dosage adjusted as necessary.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88371/', '', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Levacetylmethadol, Nalmefene, Nicotine, Buprenorphine, Naltrexone, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, Agalsidase beta, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88271, 'Metronidazole', 'Methadone', 'Minor', 'Limited data suggest that metronidazole may rarely prolong the QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. There have been isolated reports of QT prolongation and ventricular arrhythmias occurring in patients treated with metronidazole.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88372/', '', 'Nalmefene, Diamorphine, Acamprosate, Naltrexone, Naltrexone, Oxymorphone, Tapentadol, Dezocine, Opium, Pentazocine, Dihydrocodeine, Codeine', 'Tinidazole, Pantoprazole, Tetracycline, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Nizatidine, Esomeprazole, Amoxicillin, Tinidazole, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88272, 'Metyrosine', 'Methadone', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88373/', '', 'Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Acetaminophen, Dextropropoxyphene, Ibuprofen, Celecoxib, More', 'Riociguat, Ambrisentan, Macitentan, Tadalafil', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88273, 'Miconazole', 'Methadone', 'Moderate', 'Coadministration with fluconazole, voriconazole, or other azole antifungal agents may increase the serum concentrations of methadone. The mechanism is inhibition of methadone metabolism via CYP450 2C9 and/or 3A4.', 'DDInter', 'Caution is advised if methadone must be used in combination with fluconazole, voriconazole, or other azole antifungal agents. Pharmacologic response to methadone should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of azole therapy in patients who are stabilized on their methadone regimen. Patients should be advised to report excessive drowsiness, nausea, or asthenia to their physician, and to seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Caution is advised if methadone must be used in combination with fluconazole, voriconazole, or other azole antifungal agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88374/', '', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Codeine, Naltrexone, Oxymorphone, More', 'Rifamycin, Kanamycin, Amphotericin B, Polymyxin B, Nystatin, Streptomycin, Fidaxomicin, Rifaximin, Paromomycin, Neomycin, Caspofungin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88274, 'Midazolam', 'Methadone', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88375/', '', 'Acamprosate, Naltrexone, Varenicline, Nalmefene, Nicotine, Naloxone, Naltrexone, Celecoxib', 'Nitrazepam', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88275, 'Midostaurin', 'Methadone', 'Major', 'Methadone may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. High dosages of methadone alone have been associated with QT interval prolongation and torsade de pointes.', 'DDInter', 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If taking drugs that also cause CNS-depressant or orthostatic effects (e.g., sedating antihistamines, psychotropic drugs like tricyclic antidepressants, phenothiazines, and neuroleptics), patients should be made aware of the possibility of additive effects with methadone and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88376/', '', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Oxymorphone, Oxycodone, Pentazocine, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Zanubrutinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88276, 'Mifepristone', 'Methadone', 'Major', 'Mifepristone may prolong the QTc interval in a dose-related manner. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, coadministration with other drugs that can prolong the QT interval should generally be avoided. Caution is recommended if no alternatives exist and concomitant use is required. Serum potassium should be assessed prior to starting mifepristone and 1 to 2 weeks following initiation of therapy or an increase in dosage, and periodically as needed. Hypokalemia must be corrected prior to initiation of mifepristone. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hypokalemia such as fatigue, weakness, myalgia, muscle cramps, numbness, tingling, abdominal pain, constipation, palpitation, and irregular heart rhythm.', 'Synergism', 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, coadministration with other drugs that can prolong the QT interval should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88377/', '', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Codeine, Naltrexone, Oxymorphone, Tapentadol, More', 'Ulipristal, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88277, 'Milnacipran', 'Methadone', 'Moderate', 'Opioids may potentiate the effects of serotonergic agents and increase the risk of serotonin syndrome. Serotonin syndrome is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea. Since many serotonergic agents can also cause central nervous system depression, concomitant use with opioids may result in increased sedation and impairment of judgment, thinking, and psychomotor skills.', 'DDInter', 'Caution is advised when opioids are used concomitantly with serotonergic agents such as selective serotonin reuptake inhibitors (SSRIs), serotonin and norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), other antidepressants/psychotropic agents (e.g., amoxapine, buspirone, lithium, maprotiline, mirtazepine, nefazodone, trazodone, vilazodone), 5-HT1 receptor agonists (triptans), 5-HT3 receptor antagonists, cyclobenzaprine, dextromethorphan, 5-hydroxytryptophan, and St. John''s wort. Patients should be monitored for symptoms of the serotonin syndrome during treatment. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary.', 'Synergism', 'Caution is advised when opioids are used concomitantly with serotonergic agents such as selective serotonin reuptake inhibitors (SSRIs), serotonin and norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), other antidepressants/psychotropic agents (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88378/', '', 'Diamorphine, Acamprosate, Lofexidine, Varenicline, Disulfiram, Nalmefene, Nicotine, Opium, Acetaminophen, Naloxone, Celecoxib', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88278, 'Mineral oil', 'Methadone', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88379/', '', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Oxymorphone, Oxycodone, Pentazocine, More', 'Sorbitol, Magnesium sulfate, Methylnaltrexone, Tegaserod, Magnesium oxide, Naloxegol, Plecanatide, Lubiprostone, Methylcellulose, Mannitol, Naldemedine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88279, 'Minoxidil', 'Methadone', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88380/', '', 'Acamprosate, Naltrexone, Varenicline, Disulfiram, Levacetylmethadol, Nalmefene, Nicotine, Naltrexone, Tapentadol, Oxymorphone, Dezocine, More', 'Deoxycholic acid, Calcium gluconate, Pimecrolimus, Magnesium sulfate, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Diclofenac, Cromoglicic acid, Abrocitinib, More', 1767369485);
