INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88370, 'Monopotassium phosphate', 'Methadone', 'Minor', 'Urinary acidifiers may increase the renal excretion of methadone. Decreased methadone levels may result. This interaction can be used to clinical advantage in cases of methadone overdose.', 'DDInter', '-', 'Excretion', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88471/', '', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Levacetylmethadol, Nalmefene, Nicotine, Buprenorphine, Naltrexone, More', '', 1767369485);
