INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88401, 'Rifampicin', 'Methadone', 'Major', 'Coadministration with inducers of various CYP450 isoenzymes may decrease the plasma concentrations of methadone, which is metabolized by CYP450 3A4, 2B6, 2C19, 2C9, and 2D6. Reduced analgesic efficacy or withdrawal symptoms may occur in patients maintained on methadone following the addition of an inducer. Conversely, discontinuation of the inducer may increase methadone plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 'Caution is advised if methadone is prescribed with CYP450 2B6, 2C19, 2C9 and/or 3A4 inducers. Pharmacologic response to methadone should be monitored more closely whenever an inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'Caution is advised if methadone is prescribed with CYP450 2B6, 2C19, 2C9 and/or 3A4 inducers.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88502/', '', 'Diamorphine, Acamprosate, Lofexidine, Varenicline, Disulfiram, Nalmefene, Nicotine, Tapentadol, Oxymorphone, Meperidine, Dezocine, More', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88402, 'Rifapentine', 'Methadone', 'Major', 'Coadministration with inducers of various CYP450 isoenzymes may decrease the plasma concentrations of methadone, which is metabolized by CYP450 3A4, 2B6, 2C19, 2C9, and 2D6. Reduced analgesic efficacy or withdrawal symptoms may occur in patients maintained on methadone following the addition of an inducer. Conversely, discontinuation of the inducer may increase methadone plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 'Caution is advised if methadone is prescribed with CYP450 2B6, 2C19, 2C9 and/or 3A4 inducers. Pharmacologic response to methadone should be monitored more closely whenever an inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'Caution is advised if methadone is prescribed with CYP450 2B6, 2C19, 2C9 and/or 3A4 inducers.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88503/', '', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Morphine, Naltrexone, Oxymorphone, More', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88403, 'Rilonacept', 'Methadone', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88504/', '', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Levacetylmethadol, Nalmefene, Nicotine, Naltrexone, Oxymorphone, More', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, Upadacitinib, Apremilast, Inebilizumab, Cladribine, Belumosudil, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88404, 'Rilpivirine', 'Methadone', 'Major', 'Methadone may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval such as rilpivirine may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. High dosages of methadone alone have been associated with QT interval prolongation and torsade de pointes. Pharmacokinetically, coadministration of methadone and rilpivirine may modestly decrease the plasma concentrations of R(-) methadone and S(+) methadone. The proposed mechanism is induction of CYP450-mediated metabolism by rilpivirine.', 'DDInter', 'Caution is recommended when methadone is administered concomitantly with rilpivirine, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed, or if administered to patients with underlying risk factors. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope. No dosage adjustments are required when initiating rilpivirine therapy in patients stabilized on methadone. However, clinical monitoring is recommended, as methadone maintenance therapy may need to be adjusted in some patients.', 'Synergism', 'Caution is recommended when methadone is administered concomitantly with rilpivirine, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed, or if administered to patients with underlying risk factors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88505/', '', 'Diamorphine, Acamprosate, Varenicline, Disulfiram, Nalmefene, Nicotine, Oxymorphone, Oxycodone, Pentazocine, Morphine, Butorphanol, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88405, 'Risperidone', 'Methadone', 'Major', 'Methadone may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. High dosages of methadone alone have been associated with QT interval prolongation and torsade de pointes.', 'DDInter', 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If taking drugs that also cause CNS-depressant or orthostatic effects (e.g., sedating antihistamines, psychotropic drugs like tricyclic antidepressants, phenothiazines, and neuroleptics), patients should be made aware of the possibility of additive effects with methadone and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88506/', '', 'Nalmefene, Naloxone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88406, 'Ritodrine', 'Methadone', 'Major', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is advised if beta-2 adrenergic agonists are used in combination with other drugs that prolong the QT interval, including class IA and III antiarrhythmic agents, certain neuroleptic agents, phenothiazines, tricyclic antidepressants, quinolones, ketolide and macrolide antibiotics, and cisapride. It may be appropriate to monitor ECG and serum electrolytes during chronic systemic use or high-dose therapy. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Synergism', 'Caution is advised if beta-2 adrenergic agonists are used in combination with other drugs that prolong the QT interval, including class IA and III antiarrhythmic agents, certain neuroleptic agents, phenothiazines, tricyclic antidepressants, quinolones, ketolide and macrolide antibiotics, and cisapride.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88507/', '', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Oxymorphone, Oxycodone, Pentazocine, More', 'Bremelanotide, Black cohosh, Cabergoline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88407, 'Ritonavir', 'Methadone', 'Moderate', 'Some protease inhibitors have been shown to decrease the plasma concentrations of methadone.', 'DDInter', 'Caution is advised if protease inhibitors are prescribed to patients treated with methadone. Pharmacologic response to methadone should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of protease inhibitor therapy in patients who are stabilized on their methadone regimen. Patients should be advised to notify their caregiver if they experience possible symptoms of methadone withdrawal such as restlessness, insomnia, sweating, lacrimation, and rhinorrhea.', 'Others', 'Caution is advised if protease inhibitors are prescribed to patients treated with methadone.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88508/', '', 'Diamorphine, Acamprosate, Lofexidine, Nalmefene, Nicotine, Oxymorphone, Tapentadol, Dezocine, Opium, Pentazocine, Dihydrocodeine, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88408, 'Rivastigmine', 'Methadone', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', 'DDInter', 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88509/', '', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Codeine, Naltrexone, Oxymorphone, Tapentadol, Meperidine, More', 'Memantine, Aducanumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88409, 'Rizatriptan', 'Methadone', 'Moderate', 'Opioids may potentiate the effects of serotonergic agents and increase the risk of serotonin syndrome. Serotonin syndrome is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea. Since many serotonergic agents can also cause central nervous system depression, concomitant use with opioids may result in increased sedation and impairment of judgment, thinking, and psychomotor skills.', 'DDInter', 'Caution is advised when opioids are used concomitantly with serotonergic agents such as selective serotonin reuptake inhibitors (SSRIs), serotonin and norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), other antidepressants/psychotropic agents (e.g., amoxapine, buspirone, lithium, maprotiline, mirtazepine, nefazodone, trazodone, vilazodone), 5-HT1 receptor agonists (triptans), 5-HT3 receptor antagonists, cyclobenzaprine, dextromethorphan, 5-hydroxytryptophan, and St. John''s wort. Patients should be monitored for symptoms of the serotonin syndrome during treatment. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary.', 'Synergism', 'Caution is advised when opioids are used concomitantly with serotonergic agents such as selective serotonin reuptake inhibitors (SSRIs), serotonin and norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), other antidepressants/psychotropic agents (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88510/', '', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Disulfiram, Nalmefene, Nicotine, Opium, Dextropropoxyphene, Naloxone, Naltrexone', 'Rimegepant, Eptinezumab, Dihydroergotamine, Ubrogepant, Ergotamine, Galcanezumab, Atogepant, Methysergide, Erenumab, Fremanezumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88410, 'Romidepsin', 'Methadone', 'Major', 'Methadone may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. High dosages of methadone alone have been associated with QT interval prolongation and torsade de pointes.', 'DDInter', 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If taking drugs that also cause CNS-depressant or orthostatic effects (e.g., sedating antihistamines, psychotropic drugs like tricyclic antidepressants, phenothiazines, and neuroleptics), patients should be made aware of the possibility of additive effects with methadone and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Caution is recommended if methadone is used in combination with other drugs that can prolong the QT interval, particularly in the setting of chronic pain management or methadone maintenance for opioid dependency where high dosages may be employed.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88511/', '', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine, Naltrexone, Oxymorphone, Oxycodone, Pentazocine, More', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Enasidenib, Porfimer sodium, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88411, 'Ropinirole', 'Methadone', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88512/', '', 'Amantadine, Cabergoline', 'Nalmefene, Disulfiram, Acamprosate, Naltrexone, Naloxone, Naltrexone', 1767369485);
