INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88459, 'Telotristat ethyl', 'Methadone', 'Major', 'Coadministration with inducers of various CYP450 isoenzymes may decrease the plasma concentrations of methadone, which is metabolized by CYP450 3A4, 2B6, 2C19, 2C9, and 2D6. Reduced analgesic efficacy or withdrawal symptoms may occur in patients maintained on methadone following the addition of an inducer. Conversely, discontinuation of the inducer may increase methadone plasma concentrations and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 'Caution is advised if methadone is prescribed with CYP450 2B6, 2C19, 2C9 and/or 3A4 inducers. Pharmacologic response to methadone should be monitored more closely whenever an inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'Caution is advised if methadone is prescribed with CYP450 2B6, 2C19, 2C9 and/or 3A4 inducers.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88560/', '', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Varenicline, Disulfiram, Nalmefene, Morphine, Naltrexone, Oxymorphone, Tapentadol, More', '', 1767369485);
