INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88542, 'Disopyramide', 'Boceprevir', 'Major', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitor, boceprevir, may significantly increase the plasma concentrations of certain antiarrhythmic agents such as amiodarone, bepridil, disopyramide, and quinidine. The mechanism involves inhibition of CYP450 3A4 metabolism, as boceprevir is a potent inhibitor. The interaction has not been specifically studied, but could conceivably lead to serious and/or life-threatening reactions including cardiac arrhythmias and other toxicities if levels are significantly increased. The use of these antiarrhythmic agents has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', 'DDInter', 'Caution is advised if boceprevir must be used with antiarrhythmic agents that are primarily metabolized by CYP450 3A4. Pharmacologic response and plasma antiarrhythmic drug levels should be monitored more closely whenever the HCV NS3/4A protease inhibitor is added to or withdrawn from therapy, and the antiarrhythmic dosage adjusted as necessary.', 'Metabolism', 'Caution is advised if boceprevir must be used with antiarrhythmic agents that are primarily metabolized by CYP450 3A4.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88643/', '', 'Tocainide, Procainamide, Mexiletine, Bretylium, Moricizine, Ibutilide', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88543, 'Disopyramide', 'Bretylium', 'Major', 'Like other class III antiarrhythmic agents, bretylium can cause dose-related QT interval prolongation. Theoretically, coadministration with other agents that can prolong the QT interval such as disopyramide may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential related to their effects on cardiac conduction.', 'DDInter', 'Concomitant use of disopyramide with class III antiarrhythmic agents is not recommended by the manufacturer.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88644/', '', 'Quinidine, Amiodarone, Dronedarone, Tocainide, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine', 'Mexiletine, Tocainide, Moricizine, Disopyramide', 1767369485);
