INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88681, 'Disopyramide', 'Lonafarnib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of disopyramide, which is primarily metabolized by the isoenzyme. High plasma levels of disopyramide may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death. Other adverse effects such as hypoglycemic coma and ventricular fibrillation have also been reported.', 'DDInter', 'Caution is advised when disopyramide must be used concomitantly with potent CYP450 3A4 inhibitors. Pharmacologic response and disopyramide levels should be monitored more closely whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the disopyramide dosage adjusted as necessary.', 'Metabolism', 'Caution is advised when disopyramide must be used concomitantly with potent CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88782/', '', 'Tocainide, Procainamide, Flecainide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 1767369485);
