INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88769, 'Disopyramide', 'Pipecuronium', 'Moderate', 'A single case has been reported in which neostigmine reversal of vecuronium-induced paralysis was delayed in a patient who had taken disopyramide (plasma level 5.1 mcg/mL). The mechanism is unknown, but disopyramide is believed to interfere with pre- and postsynaptic neuromuscular transmission.', 'DDInter', 'While more data are needed, it is recommended that the clinician be aware of a potential interaction in patients who have taken disopyramide.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88870/', '', 'Amiodarone, Dronedarone, Tocainide, Propafenone, Procainamide, Flecainide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88770, 'Disopyramide', 'Pirbuterol', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88871/', '', 'Moricizine, Tocainide, Bretylium, Mexiletine', 'Ephedrine, Mometasone furoate, Mometasone, Fluticasone, Cromoglicic acid, Epinephrine, Beclomethasone dipropionate', 1767369485);
