INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88772, 'Disopyramide', 'Polyethylene glycol (3350)', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88873/', '', 'Lidocaine, Tocainide, Mexiletine, Bretylium, Moricizine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88773, 'Disopyramide', 'Polythiazide', 'Moderate', 'Disopyramide can cause dose-related prolongation of the QT interval. Theoretically, coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsades de pointes, because of additive arrhythmogenic potential.', 'DDInter', 'Coadministration of disopyramide with medications that can cause potassium and/or magnesium disturbances should generally be avoided. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with disopyramide. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Synergism', 'Coadministration of disopyramide with medications that can cause potassium and/or magnesium disturbances should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88874/', '', 'Quinidine, Lidocaine, Tocainide, Propafenone, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine, Ibutilide', 'Trichlormethiazide, Methyclothiazide, Tamsulosin, Tinidazole, Ascorbic acid, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Clindamycin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88774, 'Disopyramide', 'Posaconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of disopyramide, which is primarily metabolized by the isoenzyme. High plasma levels of disopyramide may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death. Other adverse effects such as hypoglycemic coma and ventricular fibrillation have also been reported.', 'DDInter', 'Caution is advised when disopyramide must be used concomitantly with potent CYP450 3A4 inhibitors. Pharmacologic response and disopyramide levels should be monitored more closely whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the disopyramide dosage adjusted as necessary.', 'Metabolism', 'Caution is advised when disopyramide must be used concomitantly with potent CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88875/', '', 'Moricizine, Tocainide, Bretylium, Mexiletine', 'Micafungin, Caspofungin, Oteseconazole, Flucytosine, Anidulafungin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (88775, 'Disopyramide', 'Potassium chloride', 'Major', 'Concomitant use of agents with anticholinergic properties (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, the class IA antiarrhythmic disopyramide) may potentiate the risk of upper gastrointestinal injury associated with oral solid formulations of potassium chloride. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents, thereby creating a high localized concentration of potassium ions in the region of a dissolving tablet or capsule and increasing the contact time with GI mucosa. Solid formulations of potassium chloride have been associated with upper GI bleeding and small bowel ulceration, stenosis, perforation, and obstruction.', 'DDInter', 'The use of oral solid formulations of potassium chloride is considered contraindicated in patients receiving agents with anticholinergic properties at sufficient doses to exert anticholinergic effects. A liquid formulation of potassium chloride should be considered. Patients prescribed a solid oral formulation should be advised to discontinue potassium therapy and contact their physician if they experience potential symptoms of upper GI injury such as severe vomiting, abdominal pain, distention, and gastrointestinal bleeding.', 'Absorption', 'The use of oral solid formulations of potassium chloride is considered contraindicated in patients receiving agents with anticholinergic properties at sufficient doses to exert anticholinergic effects.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/88876/', '', 'Tocainide, Dronedarone, Procainamide, Bretylium, Moricizine, Ibutilide', 'Potassium bicarbonate, Potassium gluconate, Calcium chloride, Zinc sulfate, Magnesium sulfate, Sodium acetate, Arginine, Sodium glycerophosphate, Zinc chloride, Tromethamine, Sodium bicarbonate, More', 1767369485);
