INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (89160, 'Valproic acid', 'Norgestrel', 'Moderate', 'A case report suggests that estrogens or progestins may decrease the serum concentrations and pharmacologic effects of valproic acid (VPA). The proposed mechanism is induction of hepatic glucuronidation by sex hormones.', 'DDInter', 'Pharmacologic response and serum valproic levels should be monitored more closely whenever estrogen- and/or progestin-containing drugs are added to or withdrawn from therapy, and the valproic acid dosage adjusted as necessary. Patients should be advised to contact their physician if they experience loss of seizure control or symptoms of valproic acid toxicity such as tremors, ataxia, nystagmus, increased seizures, and changes in mental status. In patients receiving oral contraceptives, gradual transient increases in valproic acid levels will likely occur during the pill-free week for women not also taking an enzyme-inducing drug (e.g., carbamazepine, phenytoin, phenobarbital, primidone, rifampin). The increase in valproic acid levels will be greater if the dose of valproic acid is increased in the few days before or during the pill-free week.', 'Antagonism', 'Pharmacologic response and serum valproic levels should be monitored more closely whenever estrogen- and/or progestin-containing drugs are added to or withdrawn from therapy, and the valproic acid dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/89261/', '', 'Ezogabine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, Ganaxolone, Vigabatrin, Mephenytoin, More', 'Ulipristal', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (89161, 'Valproic acid', 'Nortriptyline', 'Moderate', 'The concomitant administration of valproic acid or its derivatives may increase serum concentrations of tricyclic antidepressants. The proposed mechanism is inhibition of CYP450 hepatic metabolism. CNS and/or respiratory depressant effects may be additively or synergistically increased in patients taking multiple drugs which cause these effects. Other tricyclic antidepressants may exhibit a similar interaction. Additionally, tricyclic antidepressants may counteract the anticonvulsive effects of valproic acid and divalproex by lowering the seizure threshold.', 'DDInter', 'It may be advisable to monitor patients for altered efficacy and safety. Dose adjustments or alternate therapy may be necessary if an interaction is suspected.', 'Metabolism', 'It may be advisable to monitor patients for altered efficacy and safety.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/89262/', '', 'Lacosamide', 'Oxitriptan, Viloxazine, Tryptophan', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (89162, 'Valproic acid', 'Olanzapine', 'Moderate', 'Concurrent use of olanzapine and valproic acid may potentiate the risk of hepatotoxicity. The exact mechanism of interaction is unknown.', 'DDInter', 'The authors of the study recommend monitoring liver function tests every 3 to 4 months during the first year of treatment with either olanzapine or valproic acid, at least in pediatric patients. If no elevations of liver enzymes or marked weight gain occur after one year, a decrease in frequency of monitoring to every 6 months can be considered. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice.', 'Others', 'The authors of the study recommend monitoring liver function tests every 3 to 4 months during the first year of treatment with either olanzapine or valproic acid, at least in pediatric patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/89263/', '', 'Lacosamide', 'Amisulpride, Pimavanserin', 1767369485);
