INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (89173, 'Valproic acid', 'Paliperidone', 'Moderate', 'Coadministration with valproic acid may increase the plasma concentrations of orally administered paliperidone. The mechanism of interaction has not been established, but may involve increased gastrointestinal absorption of paliperidone. Concomitant use of paliperidone and valproic acid may increase central nervous system depressant effects such as somnolence and impairment of cognitive and psychomotor skills.', 'DDInter', 'Caution is advised during concomitant use of oral paliperidone and valproate products. Pharmacologic response to paliperidone should be monitored more closely following the initiation or withdrawal of valproate therapy, and the paliperidone dosage adjusted as necessary. No dosage adjustment for paliperidone is required when it is administered by intramuscular injection. Additionally, no dosage adjustment is necessary for valproate when coadministered with paliperidone. Patients should be advised to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism, Absorption', 'Caution is advised during concomitant use of oral paliperidone and valproate products.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/89274/', '', 'Lacosamide', 'Amisulpride, Pimavanserin', 1767369485);
