INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (89226, 'Valproic acid', 'Salicylic acid (sodium)', 'Moderate', 'Salicylates, particularly aspirin, may displace valproate from protein binding sites and inhibit its clearance. Increased therapeutic and toxic effects may be expected to occur. This interaction is more likely with large or prolonged doses of salicylates.', 'DDInter', 'Small single doses of salicylates are unlikely to cause significant effects. However, patients who take large doses of salicylates or over a prolonged period of time should be closely monitored for clinical and laboratory evidence of valproate toxicity and hepatotoxicity. Free fraction of valproate may be particularly helpful in detecting this interaction. Patients should be advised to notify their physician if they experience possible symptoms of toxicity (e.g., malaise, weakness, lethargy, drowsiness, nausea, vomiting, or abdominal pain).', 'Distribution', 'Small single doses of salicylates are unlikely to cause significant effects.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/89328/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, More', '', 1767369485);
