INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (89233, 'Valproic acid', 'Temazepam', 'Moderate', 'One case series has suggested that benzodiazepines may amplify the teratogenic effects of valproate in the offspring of epileptic women. Both drugs individually have been associated with adverse effects to the fetus. Another study has suggested that valproate may displace diazepam from plasma protein binding sites and inhibit its metabolism; however, the clinical significance has not been established. Other benzodiazepines may interact with valproate in a similar fashion.', 'DDInter', 'Both valproate and benzodiazepines should be avoided during pregnancy unless the potential benefits outweigh the risks to the fetus. In other patients, close observation for clinical evidence of benzodiazepine toxicity (excessive sedation) is recommended if valproate and a benzodiazepine must be used together.', 'Synergism', 'Both valproate and benzodiazepines should be avoided during pregnancy unless the potential benefits outweigh the risks to the fetus.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/89335/', '', 'Lacosamide', '', 1767369485);
