INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (89298, 'Dronedarone', 'Docetaxel', 'Major', 'Coadministration with amiodarone or dronedarone may significantly increase the plasma concentrations of docetaxel. The proposed mechanism is inhibition of the CYP450 3A4 and P-glycoprotein (P-gp)-mediated elimination of docetaxel by amiodarone and dronedarone.', 'DDInter', 'The use of docetaxel in combination with amiodarone or dronedarone should generally be avoided. If concomitant use is required, reduced dosage of docetaxel should be considered. Patients should also be closely monitored for the development of docetaxel toxicity such as myelosuppression, stomatitis, neurotoxicity (e.g., paraesthesia, dysesthesia, pain), myalgia asthenia, fluid retention, nausea, vomiting, and diarrhea.', 'Metabolism', 'The use of docetaxel in combination with amiodarone or dronedarone should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/89400/', '', 'Topotecan', 'Propafenone, Tocainide, Procainamide, Flecainide, Disopyramide, Dofetilide, Bretylium, Moricizine, Ibutilide', 1767369485);
