INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (90098, 'Echinacea', 'Donepezil', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', 'DDInter', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90200/', '', 'Memantine, Rivastigmine, Ginkgo biloba, Aducanumab', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (90099, 'Edrophonium', 'Donepezil', 'Moderate', 'A synergistic effect may be expected when acetylcholinesterase inhibitors are administered concurrently with other cholinesterase inhibitors or cholinergic agonists.', 'DDInter', 'Monitoring for excessive cholinergic effects and seizure activity is recommended. Patients should be advised to notify their physician if they experience nausea, vomiting, diarrhea, abdominal pain, increased salivation, cold sweats, slow or irregular heartbeat, muscle weakness or cramps, dizziness, headache, seizures, or respiratory distress.', 'Synergism', 'Monitoring for excessive cholinergic effects and seizure activity is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90201/', '', 'Memantine, Ginkgo biloba, Aducanumab', 'Methacholine, Hexaminolevulinate, Secretin human, Methylene blue, Sermorelin, Sorbitol, Galactose, Magnesium sulfate, Gonadorelin, Vitamin A, Caffeine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (90100, 'Efavirenz', 'Donepezil', 'Moderate', 'Coadministration with efavirenz may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is accelerated clearance due to induction of CYP450 3A4 activity by efavirenz.', 'DDInter', 'Caution is advised if efavirenz must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever efavirenz is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if efavirenz must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90202/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Dolutegravir, More', 'Memantine, Aducanumab', 1767369485);
