INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (90385, 'Doxepin', 'Dopamine', 'Major', 'Tricyclic antidepressants (TCAs) may markedly enhance the pressor response to parenteral direct-acting sympathomimetic agents and vasoconstrictor-containing local anesthetics.', 'DDInter', 'Parenteral administration of direct-acting sympathomimetic agents should preferably be avoided during therapy with tricyclic antidepressants except in cases of emergency (e.g., treatment of anaphylaxis). If concomitant use is necessary, initial dose and rate of administration of the sympathomimetic should be reduced, and cardiovascular status including blood pressure should be monitored closely. Although clinical data are lacking, it may be prudent to follow the same precaution with mixed-acting sympathomimetic agents.', 'Synergism', 'Parenteral administration of direct-acting sympathomimetic agents should preferably be avoided during therapy with tricyclic antidepressants except in cases of emergency (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90487/', '', 'Droxidopa, Amrinone, Angiotensin II, Milrinone, Mephentermine', 'Citalopram, Sertraline, Paroxetine, Fluoxetine, St. John''s Wort, Nefazodone, Oxitriptan, Mirtazapine, Viloxazine, Vortioxetine, Trazodone, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (90386, 'Droxidopa', 'Dopamine', 'Moderate', 'Agents that increase blood pressure such as vasopressors, triptans, decongestants, beta-2 agonists, and amphetamines may potentiate the risk of supine hypertension associated with the use of droxidopa in the treatment of neurogenic orthostatic hypotension.', 'DDInter', 'Supine blood pressure should be monitored prior to and during droxidopa treatment, and more frequently when increasing dosage. Patients should elevate the head of the bed when resting or sleeping, and have blood pressure also measured in this position. Dosage reduction or discontinuation of droxidopa is recommended if supine hypertension cannot be managed by elevation of the head of the bed. Supine hypertension that is not well managed may increase the risk of cardiovascular events, particularly stroke.', 'Synergism', 'Supine blood pressure should be monitored prior to and during droxidopa treatment, and more frequently when increasing dosage.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90488/', '', 'Droxidopa, Amrinone, Angiotensin II, Milrinone, Arbutamine, Fenoldopam', 'Dobutamine, Norepinephrine, Amrinone, Angiotensin II, Ephedrine, Milrinone, Arbutamine, Phenylephrine, Fenoldopam, Metaraminol, Epinephrine, More', 1767369485);
