INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (90406, 'Indacaterol', 'Dopamine', 'Moderate', 'Coadministration of beta-2 adrenergic agonists with other adrenergic agents may potentiate the risk of cardiovascular side effects. Beta-2 adrenergic agonists can produce clinically significant cardiovascular effects including increases in pulse rate and systolic or diastolic blood pressure as well as ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression. The risk is lower when beta-2 adrenergic agonists are inhaled at normally recommended dosages.', 'DDInter', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension. Blood pressure and heart rate should be closely monitored.', 'Synergism', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90508/', '', 'Amrinone, Angiotensin II, Milrinone, Arbutamine, Fenoldopam', 'Mometasone furoate, Umeclidinium, Budesonide, Glycopyrronium, Aclidinium, Mometasone, Epinephrine, Fluticasone, Cromoglicic acid, Ipratropium, Tiotropium, Beclomethasone dipropionate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (90407, 'Insulin human', 'Dopamine', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90509/', '', 'Droxidopa, Midodrine, Amrinone, Angiotensin II, Milrinone, Arbutamine, Fenoldopam', '', 1767369485);
