INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (90502, 'Dopamine', 'Sibutramine', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when sibutramine is combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. Sibutramine is a potent inhibitor of norepinephrine and serotonin reuptake, and a less potent inhibitor of dopamine reuptake.', 'DDInter', 'Caution is advised if sibutramine is coadministered with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating therapy with sibutramine and monitored at regular intervals thereafter. Dose reduction or discontinuation of sibutramine should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Caution is advised if sibutramine is coadministered with other drugs that can increase blood pressure and/or heart rate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/90604/', '', 'Droxidopa, Amrinone, Angiotensin II, Milrinone, Arbutamine, Fenoldopam', 'Dexfenfluramine, Naltrexone, Ephedrine, Fenfluramine, Orlistat, Lorcaserin', 1767369485);
