INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (91220, 'Doxepin', 'Quinidine', 'Moderate', 'Quinidine may markedly increase the serum levels of some tricyclic antidepressants (TCAs). The mechanism is related to inhibition of CYP450 2D6 metabolism. Extensive metabolizers of debrisoquine may be at greater risk of developing significant TCA toxicity. Desipramine, nortriptyline, and imipramine have been specifically implicated in this interaction. In addition, both quinidine and TCAs may cause prolongation of the QT interval which could result in torsades de pointes.', 'DDInter', 'If possible, this combination should be avoided. Close clinical, electrocardiographic, and laboratory monitoring is recommended for patients who do receive these drugs concurrently. Patients should be advised to promptly notify their physicians if they experience irregular heartbeats or palpitations, syncope, drowsiness, confusion, weakness, or seizures.', 'Metabolism', 'If possible, this combination should be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/91324/', '', 'Moricizine, Tocainide, Bretylium, Mexiletine', 'Desvenlafaxine, St. John''s Wort, Oxitriptan, Tryptophan, Milnacipran, Levomilnacipran, Vilazodone, Esketamine, Clemastine, Tetracaine, Mepyramine, More', 1767369485);
