INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (91471, 'Doxepin (topical)', 'Linezolid', 'Major', 'Coadministration of linezolid with serotonergic agents may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Linezolid is a reversible, nonselective monoamine oxidase inhibitor (MAOI). As such, it can enhance serotonergic effects by inhibiting serotonin metabolism.', 'DDInter', 'Concomitant use of linezolid and serotonergic agents should be avoided unless no alternatives exist and close monitoring for the development of serotonin syndrome can be provided. In general, most serotonergic psychiatric drugs should be stopped 1 to 2 weeks (i.e., 4 to 5 half-lives) prior to treatment with an MAOI, while others such as fluoxetine may require discontinuation up to 5 weeks in advance due to its prolonged half-life. Treatment with serotonergic medications may be resumed 24 hours after the last dose of linezolid.', 'Synergism', 'Concomitant use of linezolid and serotonergic agents should be avoided unless no alternatives exist and close monitoring for the development of serotonin syndrome can be provided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/91575/', '', 'Tinidazole, Bacitracin, Polymyxin B, Oritavancin, Daptomycin, Vancomycin, Telavancin, Methenamine, Spectinomycin, Nitrofurantoin, Fosfomycin, More', 'Tetracaine', 1767369485);
