INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (92942, 'Droperidol', 'Pexidartinib', 'Moderate', 'Coadministration with pexidartinib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. The proposed mechanism is increased clearance due to pexidartinib-mediated induction of CYP450 3A4 isoenzyme.', 'DDInter', 'Concomitant administration of pexidartinib with drugs that are substrates of CYP450 3A4 should generally be avoided, particularly those with a narrow therapeutic range. The potential for diminished therapeutic effects should be considered when pexidartinib is prescribed in combination with drugs that are substrates of CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring of the CYP450 3A4 substrate drug should be considered whenever pexidartinib is added to or withdrawn from therapy with these drugs.', 'Metabolism', 'Concomitant administration of pexidartinib with drugs that are substrates of CYP450 3A4 should generally be avoided, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93046/', '', 'Fluphenazine, Haloperidol, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Promazine, Thioridazine, Pimozide, Trifluoperazine, Paliperidone, More', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Tepotinib, Fedratinib, Avapritinib, Selumetinib, Cobimetinib, Ibrutinib, Afatinib, More', 1767369485);
