INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (92995, 'Droperidol', 'Secobarbital', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93099/', '', 'Nitrazepam, Zopiclone', 'Amisulpride', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (92996, 'Droperidol', 'Selegiline', 'Major', 'Anecdotal reports have implicated the concomitant use of general anesthesia and nonselective monoamine oxidase inhibitors (MAOIs) as causing hypotension or hypertension. Analysis of these cases suggests that other contributing factors may have been involved (e.g., meperidine and sympathomimetic agents).', 'DDInter', 'The makers of MAOIs, both selective and nonselective, consider general anesthesia to be contraindicated in patients treated with MAOIs and recommend discontinuing MAOI therapy 10 to 14 days before elective surgery.', 'Others', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93100/', '', 'Amisulpride, Pimavanserin', 'Amantadine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (92997, 'Droperidol', 'Selpercatinib', 'Major', 'The use of droperidol has been associated with QT interval prolongation, torsade de pointes and other serious arrhythmias, and sudden death. The concurrent administration of agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins), drugs known to increase the QT interval (e.g., phenothiazines, tricyclic antidepressants, antiarrhythmic agents, etc.), certain other drugs (benzodiazepines, volatile anesthetics, intravenous opiates), or alcohol abuse may increase the risk of prolonged QT syndrome.', 'DDInter', 'The manufacturer recommends extreme caution if droperidol must be given concomitantly with these agents. The dosage of droperidol should be individualized and titrated to the desired effect. Routine vital sign and ECG monitoring is recommended. When droperidol is used in combination with other drugs that cause CNS and/or respiratory depression, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Synergism', 'The manufacturer recommends extreme caution if droperidol must be given concomitantly with these agents.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93101/', '', 'Loxapine, Molindone, Thiothixene', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Tepotinib, Fedratinib, Avapritinib, Selumetinib, Cobimetinib, Ibrutinib, Afatinib, More', 1767369485);
