INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (93535, 'Eslicarbazepine', 'Duloxetine', 'Moderate', 'The efficacy of anticonvulsants may be diminished during coadministration with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitor (SNRIs). Antidepressants including SSRIs and SNRIs can reduce seizure threshold.Coadministration of SSRIs or SNRIs with some anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and valproic acid, may increase the risk of hyponatremia. The risk appears to be dose-related, and elderly patients and patients who are volume depleted (e.g., diuretic use) may be at greater risk.', 'DDInter', 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications. Treatment with SSRIs and SNRIs should be discontinued if seizures develop or seizure frequency increases. Patients receiving SSRIs or SNRIs with anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and/or valproic acid, should also have serum sodium levels measured regularly and monitored for development of hyponatremia, particularly when higher dosages of these medications are used. Signs and symptoms of hyponatremia include nausea, vomiting, headache, difficulty concentrating, memory impairment, confusion, malaise, lethargy, muscle weakness or spasms, and unsteadiness. In more severe and/or acute cases, hallucination, syncope, seizure, coma, respiratory arrest, and death may occur.', 'Antagonism', 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93639/', '', 'Oxitriptan, Bupropion, Viloxazine, Tryptophan', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (93536, 'Esomeprazole', 'Duloxetine', 'Minor', 'Coadministration of enteric-coated duloxetine with substances that raise gastrointestinal pH may result in earlier release of duloxetine from the formulation. The enteric coating is intended to resist drug dissolution until reaching a segment of the gastrointestinal tract where the pH exceeds 5.5. However, coadministration with aluminum- and magnesium-containing antacids (51 mEq) or famotidine has been shown to have no significant effect on the rate or extent of duloxetine absorption following administration of a 40 mg oral dose.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93640/', '', 'Sertraline, Maprotiline, Fluoxetine, Milnacipran, Vortioxetine, Levomilnacipran, Doxepin, Isocarboxazid, Viloxazine, Amoxapine, Esketamine, More', 'Tinidazole, Bismuth subcitrate potassium, Rifabutin, Vonoprazan, Levofloxacin, Chondroitin sulfate, Glucosamine', 1767369485);
