INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (93801, 'Duloxetine', 'Tamsulosin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or 2D6 may increase the plasma concentrations of tamsulosin, which is primarily metabolized in the liver by these isoenzymes.', 'DDInter', 'Caution is advised if tamsulosin is used concomitantly with moderate CYP450 3A4 inhibitors (e.g., amiodarone, aprepitant, diltiazem, dronedarone, erythromycin, fluconazole, fluvoxamine, fusidic acid, imatinib, isavuconazonium, verapamil) and/or moderate to potent CYP450 2D6 inhibitors (e.g., abiraterone, bupropion, celecoxib, cinacalcet, darifenacin, dronedarone, duloxetine, fluoxetine, lorcaserin, paroxetine, propafenone, quinidine, ranolazine, rolapitant, terbinafine), particularly at a dosage higher than 0.4 mg/day. The potential for increased risk of adverse effects such as postural hypotension, syncope, and priapism should be considered.', 'Metabolism', 'Caution is advised if tamsulosin is used concomitantly with moderate CYP450 3A4 inhibitors (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93905/', '', 'Desvenlafaxine, St. John''s Wort, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, Vilazodone, Esketamine, Escitalopram', 'Silodosin, Solifenacin, Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Ciclopirox, Darunavir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (93802, 'Duloxetine', 'Tapentadol', 'Major', 'Concomitant use of agents with serotonergic activity such as serotonin reuptake inhibitors, monoamine oxidase inhibitors, tricyclic antidepressants, 5-HT1 receptor agonists, ergot alkaloids, cyclobenzaprine, lithium, St. John''s wort, phenylpiperidine opioids, dextromethorphan, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucination, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'DDInter', 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is advised when increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life.', 'Synergism', 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/93906/', '', 'Viloxazine', 'Naloxone', 1767369485);
