INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (95224, 'Encorafenib', 'Edoxaban', 'Moderate', 'Coadministration with encorafenib may theoretically increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic cation transporter (OCT2), organic anion transporter (OAT1, OAT3), or organic anion transporting polypeptide (OATP1B1, OATP1B3). The proposed mechanism is decreased clearance due to inhibition of the corresponding transporter by encorafenib.', 'DDInter', 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever encorafenib is added to or withdrawn from therapy.', 'Excretion', 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95328/', '', 'Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Warfarin, Caplacizumab, Argatroban, Prasugrel, More', 'Entrectinib, Imatinib, Dabrafenib, Dacomitinib, Pexidartinib, Larotrectinib, Bosutinib, Ribociclib, Vandetanib, Cobimetinib, Afatinib, More', 1767369485);
