INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (95383, 'Erythromycin', 'Cisapride', 'Major', 'Coadministration with the ketolide, telithromycin, as well as certain macrolide antibiotics may significantly increase the plasma concentrations of cisapride. The mechanism is inhibition of cisapride metabolism via CYP450 3A4. High plasma levels of cisapride have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes; cardiac arrest; and sudden death. Macrolides that may significantly inhibit CYP450 3A4 include clarithromycin, erythromycin, and troleandomycin. Azithromycin and dirithromycin are generally believed to have little, if any, effect on CYP450 3A4.', 'DDInter', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, the use of cisapride with clarithromycin, erythromycin, troleandomycin, or telithromycin is considered contraindicated.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95487/', '', 'Dirithromycin, Lincomycin, Clindamycin, Minocycline, Clindamycin, Vitamin A, Isotretinoin, Dexamethasone, Fluorometholone, Tretinoin, Dapsone, More', '', 1767369485);
