INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (95545, 'Erythromycin', 'Metronidazole', 'Minor', 'Limited data suggest that metronidazole may rarely prolong the QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. There have been isolated reports of QT prolongation and ventricular arrhythmias occurring in patients treated with metronidazole.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/95649/', '', 'Lincomycin, Troleandomycin, Dirithromycin, Vitamin A, Azelaic acid, Fluorometholone, Isotretinoin, Rifamycin, Kanamycin, Oxytetracycline, Netilmicin, More', 'Tinidazole, Dexlansoprazole, Bismuth subcitrate potassium, Nizatidine, Esomeprazole, Vonoprazan, Tinidazole, Quinacrine, Secnidazole, Atovaquone, Furazolidone, More', 1767369485);
