INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (95980, 'Efavirenz', 'Nateglinide', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of nateglinide, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Because the antidiabetic effect of nateglinide is dose- and concentration-dependent, close monitoring for the development of hypoglycemia is recommended during coadministration with CYP450 2C9 inhibitors. Patients should regularly monitor their blood sugar and learn how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitation. The dosage of nateglinide may require adjustment if an interaction is suspected. Likewise, patients should be observed for potential loss of glycemic control following discontinuation of the CYP450 2C9 inhibitor, and the nateglinide dosage adjusted as necessary.', 'Metabolism', 'Because the antidiabetic effect of nateglinide is dose- and concentration-dependent, close monitoring for the development of hypoglycemia is recommended during coadministration with CYP450 2C9 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/96084/', '', 'Lamivudine, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Valaciclovir, Telaprevir, Rilpivirine, Dolutegravir, Oseltamivir, Cobicistat, More', 'Acetohexamide, Guar gum, Pramlintide, Alogliptin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Tolbutamide, Miglitol, Albiglutide, More', 1767369485);
